CREATE TABLE IF NOT EXISTS `50config` (
  `id` int(11) NOT NULL,
  `sitetitle_chs` varchar(50) COLLATE utf8_bin NOT NULL,
  `sitetitle_eng` varchar(50) COLLATE utf8_bin NOT NULL,
  `user_reg` int(2) NOT NULL,
  `user_log` int(2) NOT NULL,
  `user_edit` int(2) NOT NULL,
  `pgsize` int(11) NOT NULL,
  `hotsize` int(11) NOT NULL,
  `sitebanner_chs` varchar(80) COLLATE utf8_bin NOT NULL,
  `sitebanner_eng` varchar(80) COLLATE utf8_bin NOT NULL,
  `siteflash` varchar(50) COLLATE utf8_bin NOT NULL,
  `fvico` varchar(50) COLLATE utf8_bin NOT NULL,
  `connect` varchar(50) COLLATE utf8_bin NOT NULL,
  `sitemap` varchar(50) COLLATE utf8_bin NOT NULL,
  `lasttitle_chs` varchar(50) COLLATE utf8_bin NOT NULL,
  `lasttitle_eng` varchar(50) COLLATE utf8_bin NOT NULL,
  `lasturl_url` varchar(50) COLLATE utf8_bin NOT NULL,
  `copyrt_chs` varchar(200) COLLATE utf8_bin NOT NULL,
  `address_chs` varchar(200) COLLATE utf8_bin NOT NULL,
  `copyrt_eng` varchar(200) COLLATE utf8_bin NOT NULL,
  `address_eng` varchar(200) COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

